import createStore from 'unistore';
import axios from "axios";

const initialState = {
    listDestinasi : [],
    listHasilTriposo : [],
    apiUrl : "https://www.triposo.com/api/20190906/location.json?token=7p7uootkbeus5n7yubwk2lxaq2s7f5zk&account=POE8C0FJ&id=",
    idKota : 'Malang',
    latitudeKota : '',
    longitudeKota : '',
    isLoading: true
};

export const store = createStore(initialState);

export const actions = store => ({
    // Atur kategori dinamis untuk idKotA
	setCategory: (state, category) => {
		return {idKota : category}
    },

    // Mendefinisikan fungsi axios untuk api Tripos
	kategoriKota : async (state,event) => {
		await axios
		.get(state.apiUrl+state.idKota)
		.then(function(response) {
          store.setState({ 
              listHasilTriposo: response.data.results[0], 
              listDestinasi: response.data.results[0].images,
              latitudeKota: response.data.results[0].coordinates.latitude,
              longitudeKota: response.data.results[0].coordinates.longitude,
              idKota : response.data.results[0].name,
              isLoading: false });
          // handle success
          console.log("cek isi response",response);
          console.log("cek isi triposo",store.getState().listHasilTriposo);
          console.log("cek isi latitide kota",response.data.results[0].coordinates.latitude);
          console.log("cek isi latitude kota dari getstate",store.getState().latitudeKota);
          console.log("cek isi longitude Kota",response.data.results[0].coordinates.longitude)
		})
		.catch(function(error) {
		  store.setState({ isLoading: false });
		  // handle error
		  console.log(error);
		});
    }

});
