import React from 'react';
import '../styles/bootstrap.min.css';
import '../styles/detailDestinasi.css';

import { withRouter } from "react-router-dom";
import { connect } from "unistore/react";
import { actions } from "../store";

class Destinasi extends React.Component {

    componentDidMount = () => {
        this.props.kategoriKota()
    }



    render() {

        console.log("cek hasil dari destinasi", this.props)
        return (
            <React.Fragment>
                <div className="container-fluid" style={{textAlign:"center", border:"2px solid black"}}>
                    <h1>Navbar</h1>
                </div>
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-8" style={{textAlign:"center", border:"2px solid black"}}>
                            <div className="row"><span style={{color:"white"}}> a </span></div>
                            <div style={{textAlign:"center", borderBottom:"2px solid black"}}>
                                <h1>{this.props.idKota}</h1>
                                <span>{this.props.listHasilTriposo.snippet}
                                </span>
                                <div className="row"><span style={{color:"white"}}> a </span></div>
                            </div>
                            <div className="row"><span style={{color:"white"}}> a </span></div>
                            <div className="container destinasi-list-destinasi" >
                                <div className="row ">
                                {this.props.listDestinasi.map((isi,i)=>(
                                <React.Fragment>
                                    <div className="col-md-5 col-sm-12" style={{border:"1px solid black", margin:"10px 10px 10px 10px", borderRadius:"10%"}}>
                                        <div >
                                            <img src={isi.sizes.original.url} style={{width:"100%", 
                                            height:"100%", marginTop:"10px", borderRadius:"10%"}} alt=""/>
                                            <h5 style={{paddingTop:"10px"}}>{isi.caption}</h5>
                                        </div>
                                    </div>
                                </React.Fragment>
                                ))}
                                </div>
                            </div>
                        </div>
                        <div className="col-4" style={{textAlign:"center", border:"2px solid black"}}>
                            <div className="col-md-12"><span style={{color:"white"}}>a</span></div>
                            <div className="cari-tiket">
                                <h3>Cari Tiket</h3><br/>
                                <span className="destinasi-tiket">Lokasi Keberangkatan :</span><br/><br/>
                                <input type="text" name="kota Asal" style={{width:"100%"}} placeholder="contoh : CGK"/>
                            </div>
                            <div>
                                <img src={`https://image.maps.ls.hereapi.com/mia/1.6/mapview?apiKey=xWRMtuwOIeb403xNRnnNiKPDXNV8mgraQqDMn29r4sg&z=13&c=`+this.props.latitudeKota+","+this.props.longitudeKota} style={{width:"100%", height:"100%"}} className="App-logo" alt="logo" />
                            </div>
                        </div>
                    </div>
                </div>
            </React.Fragment>  
        )
    }
};

export default connect("latitudeKota, longitudeKota, listDestinasi, listHasilTriposo, idKota, isLoading", actions)(withRouter(Destinasi));
