import React from 'react';

import { withRouter } from "react-router-dom";
import { connect } from "unistore/react";
import { actions } from "../store";

class ListDetailDestinasi extends React.Component {

    componentDidMount = () => {
        this.props.kategoriKota()
    }

    render() {
        return (
            <React.Fragment>
                <div className="container-fluid" style={{textAlign:"center", border:"2px solid black"}}>
                     <h1>Navbar</h1>
                 </div>
                 <div className="container-fluid">
                     <div className="row">
                         <div className="col-8" style={{textAlign:"center", border:"2px solid black"}}>
                             <div className="row"><span style={{color:"white"}}> a </span></div>
                             <div style={{textAlign:"center", borderBottom:"2px solid black"}}>
                                 <h1>{this.props.idKota}</h1>
                                 <span>Lorem Ipsum is simply dummy text of 
                                     the printing and typesetting industry. Lorem Ipsum has been the industry's 
                                     standard dummy text ever since the 1500s
                                 </span>
                                 <div className="row"><span style={{color:"white"}}> a </span></div>
                             </div>
                             <div className="row"><span style={{color:"white"}}> a </span></div>
                             <div className="container">
                                 <div className="row">
                                 {this.props.listDestinasi.map((isi,i)=>(
                                    <div className="col-3" style={{border:"1px solid black"}}>
                                        <img src={isi.sizes.original.ur} style={{width:"100%", 
                                        height:"100%"}} alt=""/>
                                    </div>
                                ))}
                                </div>
                            </div>
                        </div>
                        <div className="col-4" style={{textAlign:"center", border:"2px solid black"}}>
                            <h3>Cari Tiket</h3>
                        </div>
                    </div>
                </div>
            </React.Fragment>
        )
    }
};

export default connect("listDestinasi, listHasilTriposo, idKota, isLoading", actions)(withRouter(ListDetailDestinasi));
