import createStore from 'unistore';
import axios from "axios";

const initialState = {
    listDestinasi : [],
    listHasilTriposo : [],
    apiUrl : "https://www.triposo.com/api/20190906/location.json?account=HRJIHM7V&token=p16qxx6luhn0k0fhlou5m4h52poe8c0f&id=Malang",
    idKota : 'Malang',
    latitudeKota : 'asdsada',
    longitudeKota : 'asdsad',
    mapsKota:'adsad',
    isLoading: true
};

export const store = createStore(initialState);

export const actions = store => ({
    // Atur kategori dinamis untuk idKotA
	setCategory: (state, category) => {
		return {idKota : category}
    },

    // Mendefinisikan fungsi axios untuk api Tripos
	kategoriKota : async (state,event) => {
        const destinasiUrl = state.apiUrl;
        console.log("cek Url", destinasiUrl)
		await axios
		.get("https://www.triposo.com/api/20190906/location.json?token=7p7uootkbeus5n7yubwk2lxaq2s7f5zk&account=POE8C0FJ&id=Jakarta")
		.then(function(response) {
          store.setState({ 
              listHasilTriposo: response.data.results[0], 
              listDestinasi: response.data.results[0].images,
              latitudeKota: response.data.results[0].coordinates.latitude,
              longitudeKota: response.data.results[0].coordinates.longitude,
              isLoading: false });
          // handle success
          console.log("cek isi response",response);
          console.log("cek isi triposo",store.getState().listHasilTriposo);
          console.log("cek isi listDestinasi",response.data.results[0].coordinates.latitude);
          console.log("cek isi latitude kota",store.getState().latitudeKota);
          console.log("cek isi latitudeKota",response.data.results[0].coordinates.longitude)
		})
		.catch(function(error) {
		  store.setState({ isLoading: false });
		  // handle error
		  console.log(error);
		});
    }

});
